'use client'
import React, {useEffect, useState} from 'react';
import {getDetailsMovie} from "@/app/services/detailService";
import {Button, Col, Descriptions, DescriptionsProps, Flex, Image, Row, Space, Spin} from "antd";
import {LoadingOutlined, YoutubeOutlined} from "@ant-design/icons";
import ReactPlayer from "react-player";
import {Typography} from 'antd';

const {Paragraph} = Typography;

interface MovieDetails {
    backdrop_path: string;
    name: string;
    poster_path: string;
    release_date: string | any;
    title: string;
    status: string
    runtime: number;
    vote_count: number
    tagline: string;
    overview: string;
    genres: [
        {
            name: string
        }
    ]
    production_countries: [
        {
            name: string
        }
    ]
    videos: {
        results: [
            name: string,
            key: any,
        ]
    }
}

interface DetailMovieProps {
    params: {
        id: number;
    };
}

const DetailMovie: React.FC<DetailMovieProps> = ({params}) => {
    const [data, setData] = useState<MovieDetails | null>(null);
    const [isTrailerVisible, setIsTrailerVisible] = useState(false);
    const fetchApi = async () => {
        try {
            const result = await getDetailsMovie(params.id);
            setData(result);
        } catch (error) {
            console.error("Error fetching movie details:", error);
            // Handle errors gracefully, e.g., display an error message to the user
        }
    };
    useEffect(() => {
        fetchApi();

        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [params.id]);

    const numberOfMinutes: any = data?.runtime;
    const hour: number = Math.floor(numberOfMinutes / 60); //Lấy số giờ (phần nguyên khi chia cho 60)
    const minute: number = numberOfMinutes % 60; // Lấy số phút (phần dư khi chia cho 60)
    const strTotalMinutes: string = hour + " giờ " + minute + " phút";

    const genreNames = data?.genres
        ? data?.genres.map((genre) => genre?.name)
        : [];
    const countries = data?.production_countries
        ? data?.production_countries.map((countries) => countries?.name)
        : [];
    const items: DescriptionsProps['items'] = [
        {
            key: '1',
            label: <p className={'font-bold text-white'}>Đang phát:</p>,
            children: data?.status,
        },
        {
            key: '2',
            label: <p className={'font-bold text-white'}>Năm phát hành:</p>,
            children: new Date(data?.release_date).getFullYear(),
        },
        {
            key: '3',
            label: <p className={'font-bold text-white'}>Quốc gia:</p>,
            children: countries.join(", "),
        },
        {
            key: '4',
            label: <p className={'font-bold text-white'}>Thể loại:</p>,
            children: genreNames.join(", "),
        },
        {
            key: '5',
            label: <p className={'font-bold text-white'}>Lượt đánh giá:</p>,
            children: data?.vote_count,
        },
        {
            key: '6',
            label: <p className={'font-bold text-white'}>Thời lượng:</p>,
            children: strTotalMinutes,
        },
    ];
    const handleButtonTrailer = () => {
        setIsTrailerVisible(!isTrailerVisible);
    };
    const renderTrailer = () => {
        const keyVideo = data?.videos?.results?.find(
            (video: any) =>
                video.name === "Official Trailer"
            // (video.name === "Official Trailer" ? video.name : video.name)
            // video.name
        );
        return (
            <div className="h-[210px] md:h-[400px] lg:h-[600px] xl:h-[1000px]">
                <ReactPlayer
                    light={true}
                    controls={true}
                    volume={0.5}
                    width="100%"
                    height="100%"
                    url={`https://www.youtube.com/watch?v=${keyVideo?.key}`}
                />
            </div>
        );
    };
    return (
        <>
            {!data ? <Spin indicator={<LoadingOutlined className={'text-6xl'} spin />} fullscreen /> : <Flex align={"center"} justify={"center"}>
                <Space direction={"vertical"}>
                    <Row>
                        <Col className={'relative'} xs={24} sm={24} md={24} lg={24} xl={24} xxl={24}>
                            <Image
                                className={'rounded-md object-cover'}
                                width={'100%'}
                                height={611}
                                preview={false}
                                src={`https://image.tmdb.org/t/p/w500${data?.backdrop_path}`}
                                alt={data?.title}
                                fallback="data:image/png;base64,"
                            />
                            <div className="absolute top-0 left-0 right-0 bottom-0 bg-[#0000004d] rounded-md"></div>
                            <Space className={'absolute left-3 bottom-5'} direction={"horizontal"} size={"large"}>
                                <Flex align={"end"}>
                                    <Space align={"end"} size={"large"}>
                                        <Image
                                            preview={false}
                                            width={176}
                                            className="hidden lg:block h-full rounded border-white border"
                                            fallback="data:image/png;base64,"
                                            src={`https://image.tmdb.org/t/p/w500/${data?.poster_path}`}
                                            alt={data?.title}
                                        />
                                        <Space direction={"vertical"}>
                                            <p className={'text-[28px]'}>{data?.title || data?.name}</p>
                                            {/*@ts-ignore*/}
                                            <p className={'text-base'}>({new Date(data?.release_date).getFullYear()})</p>
                                            <Space>
                                                <Button className={'rounded-none'} icon={<YoutubeOutlined/>}
                                                        type={"primary"} danger onClick={handleButtonTrailer}>Xem
                                                    Trailer</Button>
                                            </Space>
                                        </Space>
                                    </Space>
                                </Flex>
                            </Space>
                        </Col>
                        <Col xs={24} sm={24} md={24} lg={24} xl={24} xxl={24}>
                            <div className="bg-[#2e3940] text-[#adb4c1] mt-3 p-2 text-[15px]">
                                <Space direction={"vertical"}>
                                    <Descriptions
                                        items={items}
                                    />
                                    <div className="mt-2">
                                        <div>
                                            <h3 className="text-primary text-base font-semibold mt-2 mb-3">
                                                Nội dung phim
                                            </h3>
                                        </div>
                                        <h2 className="flex justify-center items-center italic mb-3">
                                            {data?.tagline}
                                        </h2>
                                        <Paragraph
                                            ellipsis={{
                                                rows: 1,
                                                expandable: true,
                                            }}
                                        >
                                            {data?.overview}
                                        </Paragraph>
                                    </div>

                                    <div className="mt-5">
                                        {isTrailerVisible && (data?.videos ? renderTrailer() : null)}
                                    </div>
                                </Space>
                            </div>
                        </Col>
                    </Row>
                </Space>
            </Flex>}
        </>
    );
};

export default DetailMovie;
